  constructor() {
    super();
    this.state = {
      id: [],
      showComponent: false,
      tutorials: [],
    };
  }
  componentDidMount() {
    axios.get(`http://localhost:8080/tutorial`).then((res) => {
      this.setState({ tutorials: res.data });
      console.log(res);
      console.log(res.data);
    });
  }

  deleteList = (tutorial) => {
    const user = { id: tutorial.id, name: tutorial.name };
    axios
      .delete(`http://localhost:8080/tutorial/${user.id}`, {
        data: user,
      })
      .then((res) => {
        console.log(res);
        let arr = this.state.tutorials;
        let result = arr.filter((ele) => ele.name !== tutorial.name);
        this.setState({ tutorials: result });
