import React from "react";
import axios from "axios";
import Modal from "react-bootstrap/Modal";
import ModalDialog from "react-bootstrap/ModalDialog";

import ModalHeader from "react-bootstrap/ModalHeader";
import ModalTitle from "react-bootstrap/ModalTitle";
import ModalBody from "react-bootstrap/ModalBody";
import ModalFooter from "react-bootstrap/ModalFooter";
import "font-awesome/css/font-awesome.min.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faEdit,
  faInfoCircle,
  faTrash,
} from "@fortawesome/free-solid-svg-icons";
import { Card, Button, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
class Cards extends React.Component {
  constructor() {
    super();
    this.state = {
      id: [], show:false,
      showComponent: false,
      tutorials: [],
    };
  }
  componentDidMount() {
    axios.get(`http://localhost:8080/tutorial`).then((res) => {
      this.setState({ tutorials: res.data });
      // console.log(res);
      // console.log(res.data);
    });
  }
  handleClose = () => {
    this.setState({ show: false });
  }
 
  handleShow = () => {
    this.setState({ show: true });
  }
  deleteList = (tutorial) => {
    const tut = { id: t._id, name: t.tName };
    console.log(tut);
    axios
      .delete(`http://localhost:8080/tutorial/${tut.id}`, {
        data: tut,
      })
      .then((res) => {
        console.log(res);
        window.location.reload();
        // let arr = this.state.tutorials;
        // console.log(arr);
        // let result = arr.filter((ele) => ele.name !== t.name);
        // this.setState({ tutorials: result });
      })
      .catch((err) => {
        console.log(err);
      });
  };
  render() {
    return (
      <>
        <Row xs={1} md={2}>
          {/* {tutorials.tName} */}
          {this.state.tutorials.map((tutorial, index) => (
            <Card>
              <Card.Header>{tutorial.tName}</Card.Header>
              <Card.Body>
                <Card.Title>{tutorial.tDesc}</Card.Title>
                <Card.Text>{tutorial.tStatus}</Card.Text>
              </Card.Body>
              <Card.Footer>
                <Link to={`/viewmore/${tutorial._id}`}>
                  <Button variant="dark">
                    <FontAwesomeIcon icon={faInfoCircle} /> View more
                  </Button>
                </Link>{" "}
                <Link to={`/edittutorial/${tutorial._id}`}>
                  <Button id="delBtn" variant="primary">
                    <FontAwesomeIcon icon={faEdit} /> Edit
                  </Button>
                </Link>{" "}
                <Button id="delBtn" variant="danger" onClick={this.handleShow}>
                  <FontAwesomeIcon icon={faTrash} /> Delete
                </Button>
              </Card.Footer>
              <Modal show={this.state.show} onHide={this.handleClose}>
                <Modal.Header closeButton>
                  <Modal.Title>Delete</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <form onSubmit={this.handlesubmit}>
                   
                    <div className="form-group">
                      <label>First Name</label>
                      <input
                        type="text"
                        name="firstname"
                        className="form-control"
                        value={this.state.firstname}
                        onChange={this.handleChange}
                        placeholder="Enter firstname"
                      />{" "}
                      <Modal.Footer>
                        <Button variant="secondary" onClick={this.handleClose}>
                          Close
                        </Button>
                        <Button
                          type="submit"
                          onClick={() => {
                            this.deleteList(tutorial);
                          }}
                          variant="primary"
                        >
                          Delete
                        </Button>
                      </Modal.Footer>
                    </div>
                  </form>
                </Modal.Body>
              </Modal>
            </Card>
          ))}
        </Row>
      </>
    );
  }
}
export default Cards;
