import axios from "axios";
import React, { Component, useState, useEffect } from "react";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";

function EditTutorial(props) {
  const url = "http://localhost:3000/Tutorialapp";
  const [tId, setTid] = useState();
  const [tName, setTname] = useState();
  const [tDesc, setTdesc] = useState();
  const [tStatus, setTstatus] = useState();

  const headers = {
    "Access-Control-Allow-Origin": "*",
    "Content-Type": "application/json",
  };

  const loadData = () => {
    let id = params.id;
    axios.get(`url/${id}`).then((res) => {
      let tutorial = res.data;
      setTid(tutorial.id);
      setTname(tutorial.tName);
      setTdesc(tutorial.tDesc);
      setTstatus(tutorial.tStatus);
    });
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleChange = (e) => {
    e.preventDefault();
    const id = document.getElementById("id").value;
    const name = document.getElementById("name").value;
    const username = document.getElementById("user-name").value;
    const email = document.getElementById("email").value;
    setId(params.id);
    setName(name);
    setUserName(username);
    setEmail(email);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let id = props.match.parama.id;
    console.log("update clicked");
    axios
      .put(url + "/" + id, {
        tName: tName,
        tDesc: tDesc,
        tStatus: tStatus,
      })
      .then((res) => {
        alert("Update Tutorial");
      });
  };

  return (
    <div className="col-md-12">
      <div className="card card-container">
        <h3 className="text-center">Edit Tutorial</h3>
        <Form onSubmit={handleSubmit}>
          <div>
            <div className="form-group">
              <label htmlFor="tutorial_id">Tutorial Id</label>
              <Input
                type="text"
                className="form-control"
                id="tutorial_id"
                name="tutorial_id"
                value={tId}
                onChange={(event) => setTid(event.target.value)}
                placeholder="Id"
                disabled
              />
            </div>

            <div className="form-group">
              <label htmlFor="tutorial_name">Tutorial Name</label>
              <Input
                type="text"
                className="form-control"
                id="tutorial_name"
                name="tutorial_name"
                value={tName}
                onChange={(event) => setTname(event.target.value)}
                placeholder="Tutorial Name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="tutorial_desc">Tutorial Description</label>
              <Input
                type="text"
                className="form-control"
                id="tutorial_desc"
                name="tutorial_desc"
                value={tDesc}
                onChange={(event) => setTdesc(event.target.value)}
                placeholder="Tutorial Description"
              />
            </div>
            <div className="form-group">
              <label htmlFor="tutorial_status">Status</label>
              <select className="form-select form-control">
                <option
                  value="Published"
                  onChange={(event) => (setTstatus) => event.target.value}
                >
                  Published
                </option>
                <option
                  value="UnPublished"
                  onChange={(event) =>
                    console.log((setTstatus) => event.target.value)
                  }
                >
                  UnPublished
                </option>
              </select>
            </div>

            <div className="form-group">
              <button type="submit" className="btn btn-primary btn-block">
                Add
              </button>
            </div>
          </div>
        </Form>
      </div>
    </div>
  );
}
export default EditTutorial;
